import React, { useState, useEffect } from "react";
import axios from "axios";

import Product from "../components/Product";
export default function HomeScreen() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  // if there is any error in fetching data from backend
  const [error, setError] = useState(false);
  useEffect(() => {
    // send ajax request to backend and fetch products using async function
    const fetchData = async () => {
      try {
        setLoading(true);
        // the array in backend converts into data n frontend
        const { data } = await axios.get("/api/products");
        setLoading(false);
        setProducts(data);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };
    fetchData();
  }, []);
  return (
    <div>
      {loading}?<LoadingBox></LoadingBox>: error?
      <MessageBox variant="danger">{error}</MessageBox>:
      <div className="row center">
        {products.map((product) => (
          <Product key={product._id} product={product}></Product>
        ))}
      </div>
    </div>
  );
}
