import {
  CART_ADD_ITEM,
  CART_REMOVE_ITEM,
  CART_SAVE_SHIPPING_ADDRESS,
} from "../constants/cartConstants";
import Axios from "axios";

//adding addToCart action function
export const addToCart = (productId, qty) => async (dispatch, getState) => {
  //sending Ajax request to server to get informtion about product
  const { data } = await Axios.get(`/api/products/${productId}`);
  //after getting data dispatch an action
  //by dispatching this action we requesting redux store to add this product to cart
  dispatch({
    type: CART_ADD_ITEM,
    payload: {
      name: data.name,
      image: data.image,
      price: data.price,
      countInStock: data.countInStock,
      product: data._id,
      qty,
    },
  });
  //even after refreshing to maintain the cart items use localstoragae
  //value should be string not object thats y stringy is used
  localStorage.setItem("cartItems", JSON.stringify(getState().cart.cartItems));
};
export const removeFromCart = (productId) => (dispatch, getState) => {
  dispatch({ type: CART_REMOVE_ITEM, payload: productId });
  localStorage.setItem("cartItems", JSON.stringify(getState().cart.cartItems));
};
//function returning another function
export const saveShippingAddress = (data) => (dispatch) => {
  dispatch({ type: CART_SAVE_SHIPPING_ADDRESS, payload: data });
  localStorage.setItem("shippingAddress", JSON.stringify(data));
};
